    def button_dut_ptz_up(self):
        self.ST.configure(state="normal")
        self.ST.insert(tk.END, 'DUT Camera Move Up' + '!\n')
        # Create an ONVIF camera object
        camera = ONVIFCamera('172.20.10.3', 2020, 'IntelANIL', 'Lad12345')

        # Create a media service object
        media_service = camera.create_media_service()

        # Get the media profile
        profiles = media_service.GetProfiles()
        media_profile = profiles[0]

        # Create a PTZ service object
        ptz_service = camera.create_ptz_service()

        # Get PTZ configuration options for the media profile
        request = ptz_service.create_type('GetConfigurationOptions')
        request.ConfigurationToken = media_profile.PTZConfiguration.token
        ptz_configuration_options = ptz_service.GetConfigurationOptions(request)

        # Define a function to perform relative PTZ movements
        def perform_relative_ptz_move_up(ptz_service, media_profile, pan, tilt, zoom):
            request = ptz_service.create_type('RelativeMove')
            request.ProfileToken = media_profile.token

            # Set the relative pan, tilt, and zoom movements
            request.Translation = {
                'PanTilt': {'x': pan, 'y': tilt},
                'Zoom': {'x': zoom}
            }

            # Perform the PTZ move
            ptz_service.RelativeMove(request)

            # Wait for the movement to complete
            import time
            time.sleep(1)

        # Example usage: Pan right by 0.1, tilt up by 0.1, and zoom in by 0.1
        perform_relative_ptz_move_up(ptz_service, media_profile, pan=0, tilt=0.1, zoom=0.1)
        #self.ST.configure(state="disabled")
        
